package org.meveo.script;

import java.io.File;
import java.io.IOException;
import java.nio.file.Files;
import java.nio.file.LinkOption;
import java.nio.file.Path;
import java.nio.file.StandardCopyOption;
import java.util.Arrays;
import java.util.Map;
import java.util.Objects;

import org.meveo.service.script.Script;
import org.meveo.admin.exception.BusinessException;
import org.meveo.commons.utils.FileUtils;
import org.meveo.commons.utils.ParamBean;
import org.meveo.commons.utils.ParamBeanFactory;
import org.meveo.model.BusinessEntity;
import org.meveo.model.customEntities.CustomEntityInstance;
import org.meveo.model.customEntities.CustomEntityTemplate;
import org.meveo.model.git.GitRepository;
import org.meveo.security.MeveoUser;
import org.meveo.service.custom.CustomEntityInstanceService;
import org.meveo.service.custom.CustomEntityTemplateService;
import org.meveo.service.git.GitClient;
import org.meveo.service.git.GitHelper;
import org.meveo.service.git.GitRepositoryService;
import org.meveo.service.git.MeveoRepository;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;

public class GenerateCustomEntityGUIScript extends Script {

    private static final Logger LOG = LoggerFactory.getLogger(GenerateCustomEntityGUIScript.class);
    private CustomEntityInstanceService customEntityInstanceService = getCDIBean(CustomEntityInstanceService.class);
    private CustomEntityTemplateService customEntityTemplateService = getCDIBean(CustomEntityTemplateService.class);
    private GitRepositoryService gitRepositoryService = getCDIBean(GitRepositoryService.class);
    private GitRepository entityGUIRepository;
    private CustomEntityInstance config;
    private MeveoUser currentUser;

    @Override
    public void execute(Map<String, Object> parameters) throws BusinessException {
        super.execute(parameters);

        LOG.debug("GenerateCustomEntityGUIScript ==> START");

        config = (CustomEntityInstance) parameters.get("config");
        currentUser = (MeveoUser) parameters.get(CONTEXT_CURRENT_USER);

        LOG.debug("ENTITY CONFIG: {}", config.toString());

        try {
            setup();
        } catch (IOException ioe) {
            throw new BusinessException(ioe);
        }

        LOG.debug("GenerateCustomEntityGUIScript ==> END");
    }

    private void setup() throws BusinessException, IOException {
        entityGUIRepository = gitRepositoryService.findByCode("custom_entity");
        if (entityGUIRepository == null) {
            ParamBeanFactory paramBeanFactory = getCDIBean(ParamBeanFactory.class);
            ParamBean appConfig = paramBeanFactory.getInstance();

            String remoteUrl = appConfig.getProperty("meveo.git.directory.remote.url", null);
            String remoteUsername = appConfig.getProperty("meveo.git.directory.remote.username", null);
            String remotePassword = appConfig.getProperty("meveo.git.directory.remote.password", null);

            entityGUIRepository = new GitRepository();
            entityGUIRepository.setCode("custom_entity");
            entityGUIRepository.setRemoteOrigin(remoteUrl);
            entityGUIRepository.setDefaultRemoteUsername(remoteUsername);
            entityGUIRepository.setDefaultRemotePassword(remotePassword);

            gitRepositoryService.create(entityGUIRepository);

            // save reference to new git repository
            entityGUIRepository = gitRepositoryService.findByCode("custom_entity");
        }
        File repositoryDirectory = GitHelper.getRepositoryDir(this.currentUser, entityGUIRepository.getCode());
        Path repositoryPath = repositoryDirectory.toPath();

        LOG.debug("repository path: {}", repositoryPath);

        // add template files
        CustomEntityTemplate templatePathCET = customEntityTemplateService.findByCodeOrDbTablename("TEMPLATE_PATH");
        CustomEntityInstance templatePathCEI = customEntityInstanceService.findByCodeByCet(templatePathCET.getCode(),
                "ENTITY_GUI_TEMPLATE_PATH");
        String templatePath = templatePathCEI.getCfValuesNullSafe().getCfValue("path").getStringValue();
        File templatesDirectory = new File(templatePath);
        Path templatesPath = templatesDirectory.toPath();

        Files.copy(templatesPath, repositoryPath, StandardCopyOption.REPLACE_EXISTING,
                StandardCopyOption.COPY_ATTRIBUTES, LinkOption.NOFOLLOW_LINKS);

        LOG.debug("templates path: {}", templatePath);
    }
}
